        },
        {
          "odrl:leftOperand": "odrl:dateTime",
          "odrl:operator": {
            "@id" : "odrl:gteq"
          },
          "odrl:rightOperand": {"@value": "2025-08-31T00:00:00Z", "@type": "xsd:dateTime"}
