      {
        "@value": "Bike",
        "@type": "xsd:string"
      },
      {
        "@value": "Santander",
        "@type": "xsd:string"
      }
    ],
    "dct:spatial" : {
      "@id": "http://www.wikidata.org/entity/Q12233",
      "@type": "dct:Location"
    },
    "sedimark:isProvidedBy" : {
      "@id": "https://uc.sedimark.eu/offerings/dummy-offering-id/assetProvision/dummy-assetProvision-id",
      "@type": "sedimark:AssetProvision",
      "dct:title": {
        "@value": "Bike 1 asset provision",
        "@type": "xsd:string"
      },
      "dct:format": {
        "@id": "HttpData"
      },
      "dct:description": {
        "@value": "This asset provision allows access to data from Bike 1 through a REST API",
        "@type": "xsd:string"
      },
      "dct:issued": {
        "@value": "2025-12-31T00:00:00Z",
        "@type": "xsd:dateTime"
      },
      "dcat:accessURL": {
        "@id": "http://provider-domain-private-endpoint"
      },
      "sedimark:headers" : [
        {
          "sedimark:headerName": {
            "@value": "Accept",
            "@type": "xsd:string"
          },
          "sedimark:headerValue": {
            "@value": "application/ld+json",
            "@type": "xsd:string"
          }
        }
        ]
    }
  }
  ]
}

